import { Router } from '@angular/router';
import { ReceitasService } from './../../../services/receitas.service';
import { IReceita } from './../../../model/IReceita.model';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-cadastrar-receitas',
  templateUrl: './cadastrar-receitas.component.html',
  styleUrls: ['./cadastrar-receitas.component.css']
})
export class CadastrarReceitasComponent implements OnInit{
  receita: IReceita ={
    nome: '',
    precoReceita: 0
  };

  constructor(private receitasService: ReceitasService, private router: Router) {}

  ngOnInit(): void {}

  salvarReceita(): void {
      this.receitasService.cadastrar(this.receita).subscribe(retorno => {
        this.receita = retorno;
        this.receitasService.exibirMensagem(
          'Sistema',
          '$(this.receita.nome) foi cadastrado com sucesso. ID: $(this.receita.id)',
          'toast-success'
        );
        this.router.navigate(['/receitas']);
      });
  }


}
<h1 class="my-3 text-primary">Cadastrar Receita</h1>
<div class="card shadow">
  <form class="m-3">
    <div class="row">
      <div class="col-md-12">
        <div class="form-group">
          <label for="nome">Nome da Receita:</label>
          <input
            type="text"
            class="form-control"
            placeholder="Digite o nome da Receita"
            name ="nomeReceita"
            [(ngModel)]="receita.nome"
          />
        </div>
      </div>
    </div> <!--fim row-->
    <div class="row">
      <div class="col-md-6">
        <div class="form-group">
          <label for="prcoProduto">Preço da Receita:</label>
          <input
          type="number"
          class="form-control"
          placeholder="0.00"
          name="preco"
          [(ngModel)]="receita.precoReceita"
          />
        </div>
      </div>
    </div>
<div class="row">
  <div class="col">
    <button class="btn btn-default" routerLink="/receitas">Voltar</button>
    <button class="btn btn-warning float-right" (click)="salvarReceita()">
      Salvar
    </button>
  </div>
</div>

  </form>
</div>
